	void Map::LoadBitmap() {
		midground.LoadBitmap(IDB_MIDGROUND1);
		test.LoadBitmap(IDB_BITMAP9);
	}

	void Map::Initialize() {
		midground.SetTopLeft(0, 0);
	}

	void Map::OnShow() {
		midground.ShowBitmap();
		for (int row = 0; row < 80; row++) {
			for (int col = 0; col < 60; col++) {
				switch (mapGrid[row][col])
				{
				case 0:
					break;
				case 1:
					test.SetTopLeft(10 * row, 10 * col);
					test.ShowBitmap();
					break;
				case 2:
					break;
				default:
					break;
				}
			}
		}
	}

	void Map::inMap() {
		mapnum = mapnum + 1;
	}

	bool Map::isEmpty(int mapX, int mapY) {
		int gridX = mapX / 10;
		int gridY = mapY / 10;
		return mapGrid[gridX][gridY] == 0;
	}
